import React, { Component } from "react";
import $ from "jquery";

export default class RiskHeader extends Component {
  constructor(props) {
    super(props);
    this.state = {
      CompanyRiskCategory: "",
      riskClass:'',
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }
  componentDidMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({ CompanyRiskCategory: data[0].RiskCategoryName });
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });
  }

  render() {
    return (
      <React.Fragment>
        <div className="riskTypeBar noRisk">
          <span>{this.state.CompanyRiskCategory}</span>
        </div>
      </React.Fragment>
    );
  }
}
