import React, { Component } from "react";
import { render } from 'react-dom';
import $ from "jquery";
import HighchartsReact from 'highcharts-react-official';
import Highcharts from 'highcharts';

export default class CardContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Categories: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }

  componentWillMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
          Categories: data
        });
        //this.applyChart(data);
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });

    document.documentElement.className +=
		(("ontouchstart" in document.documentElement) ? 'touch-app' : 'no-touch-app');
  }

   applyChart(stats) {
		//Common chart options for risk cards
		var chartOptions = {
			chart: {
				plotBackgroundColor: null,
				plotBorderWidth: null,
				plotShadow: false,
				type: 'pie',
				margin: 0,
				defaultSeriesType: 'areaspline',
				backgroundColor: '#F2F5F8',
				spacingLeft: 0
			},
			title: {
				text: ''
			},

			accessibility: {
				point: {
					valueSuffix: '%'
				}
			},
			plotOptions: {
				pie: {
					allowPointSelect: false,
					cursor: 'pointer',
					dataLabels: {
						enabled: false
					},
					showInLegend: false
				}
      },
      series: [{
        name: '',
        //colorByPoint: true,
        data: [{
          name: 'Passed',
          y: stats.PassedCount,
          //sliced: false,
          //selected: false,
          color: '#008000'
        }, {
          name: 'Failed',
          y: stats.FailedCount,
          color: '#FF0000'
        }, {
          name: 'Unanswered',
          y: stats.Unanswered,
          color: '#638089'
        }]
      }]
    }
    return chartOptions;
		// Build the chart
		// for (var cardIndex = 0; cardIndex < stats.length; cardIndex++) {
		// 	var chartID = 'CardChartContainer' + (cardIndex + 1);
		// 	Highcharts.chart(chartID, {
		// 		chart: chartOptions.chart,
		// 		title: chartOptions.title,
		// 		accessibility: chartOptions.accessibility,
		// 		plotOptions: chartOptions.plotOptions,
		// 		series: [{
		// 			name: '',
		// 			//colorByPoint: true,
		// 			data: [{
		// 				name: 'Passed',
		// 				y: stats[cardIndex].PassedCount,
		// 				//sliced: false,
		// 				//selected: false,
		// 				color: '#008000'
		// 			}, {
		// 				name: 'Failed',
		// 				y: stats[cardIndex].FailedCount,
		// 				color: '#FF0000'
		// 			}, {
		// 				name: 'Unanswered',
		// 				y: stats[cardIndex].Unanswered,
		// 				color: '#638089'
		// 			}]
		// 		}]
		// 	});
		// }
	};

  assessmentInfo(data) {
		if (data.PassedCount + data.FailedCount === 0) {
			return "This assessment hasn't been started yet.";
		}
		else
			return data.Percentage + '% completed';
  }
  assessmentStatus(data) {
		if (data.PassedCount + data.FailedCount === 0) {
			return 'Start Assessment';
		}
		else if (data.Unanswered > 0) {
			return 'Continue Assessment';
		}
		else if (data.Unanswered === 0) {
			return 'Review Assessment';
		}

		return '';
	};

  render() {
    
    const eachCard = this.state.Categories.map((item, index) => (
      <div className="col-md-4 col-sm-12 cardColumn" key={index}>
        <div className="risk_card">
          <button type="button" className="actionButton">
            {item.RiskCategoryName}
          </button>
          <h4 className="risk_card_title">{item.CategoryName}</h4>
          <div className="risk_card_pie_sec row">
            <div className="col-md-6 col-6 fullWidthAndHeight">
              <div
                id={"CardChartContainer"+(index+1)}
                className="fullWidthAndHeight"
              >
                <HighchartsReact highcharts={Highcharts} options={this.applyChart(item)}/>
              </div>
              
            </div>
            <div className="col-md-6 col-6">
              <table className="legendsTable">
                <tbody>
                  <tr>
                    <td>
                      <strong>{item.PassedCount}</strong>
                    </td>
                    <td>
                      <span className="legendCircle legendPassed">&nbsp;</span>
                    </td>
                    <td>judged as Passed</td>
                  </tr>
                  <tr>
                    <td>
                      <strong>{item.FailedCount}</strong>
                    </td>
                    <td>
                      <span className="legendCircle legendfailed">&nbsp;</span>
                    </td>
                    <td>judged as Failed</td>
                  </tr>
                  <tr>
                    <td>
                      <strong>{item.Unanswered}</strong>
                    </td>
                    <td>
                      <span className="legendCircle">&nbsp;</span>
                    </td>
                    <td>not judged yet</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="clearfix"></div>
          </div>
          <div
          className="risk_card_overlay overlaySec"
          id={"sysAnddelivaryCardLayer" + index}>
          <div>
            <h4 className="risk_card_title">{item.CategoryName}</h4>
          </div>
          <div style={{ margin: "30px 0 15px" }}>{this.assessmentInfo(item)}</div>
          <button className="actionButton">{this.assessmentStatus(item)}</button>
        </div>
        </div>
        <div className="risk_card_mobile_overlay overlaySec">

					<div className="inlineMiddile" style={{width:'calc(100% - 205px)'}}>{this.assessmentInfo(item)}</div>
					<button className="actionButton inlineMiddile">{this.assessmentStatus(item)}</button>
				</div>
      </div>
    ));
    return (
      <React.Fragment>
        <div className="row cardsRow">{eachCard}</div>
      </React.Fragment>
    );
  }
}
render(<CardContainer />, document.getElementById('root'));
