import React, { Component } from "react";
import $ from "jquery";

export default class CardContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      CompanyRiskCategory: "",
      firstRecord: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }

  componentDidMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
          CompanyRiskCategory: data[0].RiskCategoryName,
          firstRecord: data[0]
        });
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });
  }

  render() {
    return <React.Fragment>Will display slowly</React.Fragment>;
  }
}
