import React, { Component } from "react";
import $ from "jquery";

export default class RiskHeader extends Component {
  constructor(props) {
    super(props);
    
    this.state = {
      CompanyRiskCategory: "",
      firstRecord: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
   
  }
   

  componentWillMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
          CompanyRiskCategory: data[0].RiskCategoryName,
          firstRecord: data[0]
        });
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }
    });
  }

  renderSwitch(item){
      console.log('class compute',item)
        let itemName = item?item.RiskCategoryName:'';
    switch (itemName) {
        case "Moderate Risk":
          return "moderateRisk noBorder";
        case "Low Risk":
          return "lowRisk noBorder";
        case "Moderate-High Risk":
          return "highModerateRisk noBorder";
        case "High Risk":
          return "highRisk noBorder";
        default:
          return "";
      };
  };

  render() {
        return (
      <React.Fragment>
        <div className={"riskTypeBar noRisk "+(this.renderSwitch(this.state.firstRecord))}>
          <span>{this.state.CompanyRiskCategory}</span>
        </div>
      </React.Fragment>
    );
  }
}
