import React, { Component } from "react";
import $ from "jquery";

export default class CardContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
        Categories: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }

  componentDidMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
            Categories: data
        });
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });
  }

  render() {
      const eachCard = this.state.Categories.map((item)=>
            <div className="col-md-4 col-sm-12 cardColumn">
                <div className="risk_card">
                    <button type="button" className="actionButton">{item.RiskCategoryName}</button>
                </div>
            </div>
      );
    return (
    <React.Fragment>
        {eachCard}
    </React.Fragment>
    );
  }
}
