import React, { Component } from "react";
import HeaderMenu from "./headermenu";
import Footer from "./footer";
import RiskHeader from "./riskHeader";
import CardContainer from "./cardConatiner";

import $ from "jquery";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Categories: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }
  componentDidMount () {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
          Categories: data
        });
       console.log(data)
      }.bind(this),
      error: function(error) {
        console.log(error);
      }
    });
   
  }

  render() {
    let Categories =[this.state.Categories];let riskElement,cardElement;
    if(this.state.Categories.length){ riskElement = <RiskHeader Categories={Categories}/>; cardElement = <CardContainer Categories={Categories} {...this.props}/>}
    console.log('history will be',this.props.history, Categories);
    return (
      <div>
        <div className="fixedHeader">
          <header>
            <HeaderMenu />
          </header>
          <section className="dashboard_sec flexAsColumn">
            <div className="mainWrapperAsContainer dashboardHeader">
              Dashboard - Business Risk Review +
            </div>
          </section>
          {riskElement}
        </div>
        <div
          className="mainWrapperAsContainer main_content_sec"
          id="mainContent"
        >
         {cardElement}
          
        </div>
        <footer>
          <Footer />
        </footer>
      </div>
    );
  }
}
