import React, { Component } from "react";
import $ from "jquery";

export default class RiskHeader extends Component {
  constructor(props) {
    super(props);
    this.createRiskClass = this.createRiskClass.bind(this);
    this.state = {
      CompanyRiskCategory: "",
      riskClass: "undefined",
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
    this.createRiskClass = this.createRiskClass.bind(this)
  }

 createRiskClass(item) {
    
    var itemName = this.state.CompanyRiskCategory;
    if (item) {
      itemName = item.RiskCategoryName;
    }
    this.setState({
        riskClass:(()=>{
            switch (itemName) {
                case "Moderate Risk":
                  return "moderateRisk noBorder";
                case "Low Risk":
                  return "lowRisk noBorder";
                case "Moderate-High Risk":
                  return "highModerateRisk noBorder";
                case "High Risk":
                  return "highRisk noBorder";
                default:
                  return "";
              };
        })
    })
   
  }

  componentDidMount() {
      console.log(`${this.state.serviceBaseUrl}/Categories/RiskProfiles`)
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
          CompanyRiskCategory: data[0].RiskCategoryName,
          riskClass: this.createRiskClass(this,data[0])
        });
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });
  }

  

  render() {
        return (
      <React.Fragment>
        <div className={"riskTypeBar noRisk "+(this.state.riskClass)}>
          <span>{this.state.CompanyRiskCategory}</span>
        </div>
      </React.Fragment>
    );
  }
}
