import React, { Component } from "react";
import $ from "jquery";

export default class CardContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
        Categories: [],
      serviceBaseUrl:
        "https://gtz-proj-neo-01.southindia.cloudapp.azure.com/JsonToWebAPI2/v1"
    };
  }

  componentDidMount() {
    $.ajax({
      url: `${this.state.serviceBaseUrl}/Categories/RiskProfiles`,
      dataType: "json",
      success: function(data) {
        this.setState({
            Categories: data
        });
        console.log(data[0]);
      }.bind(this),
      error: function(error) {
        console.log(error);
      }.bind(this)
    });
  }

  render() {
      const eachCard = this.state.Categories.map((item,index)=>
            <div className="col-md-4 col-sm-12 cardColumn">
                <div className="risk_card">
                    <button type="button" className="actionButton">{item.RiskCategoryName}</button>
                    <h4 className="risk_card_title">{item.CategoryName}</h4>
                    <div className="risk_card_pie_sec row">
                    <div class="col-md-6 col-6 fullWidthAndHeight">
							<div id="'CardChartContainer'+{index+1}" className="fullWidthAndHeight"></div>
						</div>
						<div className="col-md-6 col-6" style={{paddingLeft: "0px"}}>
							<table class="legendsTable">
								<tr>
									<td><strong data-bind="text:PassedCount"></strong></td>
									<td><span class="legendCircle legendPassed">&nbsp;</span></td>
									<td>judged as Passed</td>
								</tr>
								<tr>
									<td><strong data-bind="text:FailedCount"></strong></td>
									<td><span class="legendCircle legendfailed">&nbsp;</span></td>
									<td>judged as Failed</td>
								</tr>
								<tr>
									<td><strong data-bind="text:Unanswered"></strong></td>
									<td><span class="legendCircle">&nbsp;</span></td>
									<td>not judged yet</td>
								</tr>
							</table>
						</div>
						<div class="clearfix"></div>
                    </div>
                </div>
            </div>
      );
    return (
    <React.Fragment>
        <div className="row cardsRow">
        {eachCard}
        </div>
        
    </React.Fragment>
    );
  }
}
